mapper.select:
		 select *
	from(
        select rownum rn, a.*
        from ( 
                 select *
                from board order by board_num desc
                ) a
            )b
	where b.rn > (#{page} - 1) * 10 

	and     b.rn <= #{page} * 10

mapper.update:
update board
set board_title = #{boardTitle},
	board_content = #{boardContent},
	board_date = #{boardDate}
where board_num = #{boardNum}
